CREATE TABLE shopping_commodity_info (id varchar(50) NOT NULL COMMENT '编号', name varchar(10) COMMENT '名称', describe varchar(100) COMMENT '描述', classify varchar(50) COMMENT '分类', repertorynum varchar(100) COMMENT '库存数量', purchaseprice decimal(17,6) COMMENT '进货价格', offeringprice decimal(17,6) COMMENT '出售价格', isenabled varchar(10) COMMENT '是否有效', inputdate timestamp DEFAULT CURRENT_TIMESTAMP NULL COMMENT '插入日期', updatedate timestamp NULL COMMENT '更新时间', PRIMARY KEY (id)) ENGINE=InnoDB DEFAULT CHARSET=utf8mb4 COMMENT='商品表';
