CREATE TABLE shopping_customer_info (id varchar(50) NOT NULL COMMENT '编号', name varchar(10) COMMENT '姓名', gender varchar(10) COMMENT '性别', phone varchar(50) COMMENT '电话', address varchar(100) COMMENT '地址', photourl varchar(100) COMMENT '头像url', status varchar(10) COMMENT '用户状态', inputdate timestamp DEFAULT CURRENT_TIMESTAMP NULL COMMENT '插入日期', updatedate timestamp NULL COMMENT '更新时间', PRIMARY KEY (id)) ENGINE=InnoDB DEFAULT CHARSET=utf8mb4 COMMENT='用户表';
