CREATE TABLE shopping_order_info (serialno varchar(50) NOT NULL COMMENT '流水号', customerid varchar(50) NOT NULL COMMENT '订单所属用户编号', initamount decimal(17,6) COMMENT '初始金额', discountamount decimal(17,6) COMMENT '优惠金额', freightcharge decimal(17,6) COMMENT '运费金额', totalamount decimal(17,6) COMMENT '总金额', modeofpayment varchar(50) COMMENT '支付方式', isenabled varchar(50) COMMENT '是否有效', inputdate timestamp DEFAULT CURRENT_TIMESTAMP NULL COMMENT '录入日期', updatedate timestamp NULL COMMENT '更新时间', PRIMARY KEY (serialno)) ENGINE=InnoDB DEFAULT CHARSET=utf8mb4 COMMENT='订单表';
