CREATE TABLE shopping_order_relation (serialno varchar(50) NOT NULL COMMENT '流水号', orderserialno varchar(50) COMMENT '订单关联流水号', commodityid varchar(50) COMMENT '商品关联流水号', commoditycount varchar(50) COMMENT '货品数量', inputdate timestamp DEFAULT CURRENT_TIMESTAMP NULL COMMENT '录入日期', updatedate timestamp NULL COMMENT '更新时间', PRIMARY KEY (serialno)) ENGINE=InnoDB DEFAULT CHARSET=utf8mb4 COMMENT='订单-商品关联表';
